    scanf("%s", str);
    int n = strlen(str);
    for(i=0;i<n;i++)
    {
        if(str[i] == 'a')
        {
            count++;
        }
    }
    int m = n;
    int ans;
    int half = n/2;
    if(count > half)
    {
        printf("%d",n);
    }
    if(count <= half)
    {
        while(m > count)
        {
            m = n/2;
            n--;
            ans = n;
        }
        printf("%d",ans-1);
    }
return 0;
}

