void map()
{
    int i,j,count = 0,a,b,c,r;
    scanf("%d %d %d %d",&a,&b,&c,&r);


        int first = c-r;

    int size = 2*r+1;

    int arr[size];

    for(i=0;i<(size);i++)
    {
        arr[i] = first;
        first++;
    }

    int s = diff(a,b)+1;

    int res[s];

    int m = min(a,b);

    for(i=0;i<s;i++)
    {
        res[i] = m;
        m++;
    }

    for(i=0;i<size;i++)
    {
        for(j=0;j<s;j++)
        {
            if(arr[i] == res[j])
                {
                    count++;
                }
        }
    }
    int ans = s-count;
    printf("%d\n",ans);



    //int ans = b-a-count;
    //printf("%d\n",count);
}
