int fib(int n) 
{ 

    int f[n + 1]; 
    int i; 


    f[0] = 9; 
    f[1] = 31; 

    for (i = 2; i <= n; i++) { 

        f[i] = (8*f[i - 1])% 1000000007 - (15*f[i - 2])% 1000000007; 
    } 

    return f[n]; 
} 

