#include<stdio.h>
#include<stdlib.h>
#include<string.h>
#define MAX 3000


int top = -1;
int stack[MAX];


void push(char item)
{
    top += 1;
    stack[top] = item;
}


void pop()
{
    top--;    
}


int main()
{
    char filename[50];
    char data[3000];

    printf("Enter the name of the file : ");
    scanf("%s",filename);

    FILE *fptr1 , *fptr2;

    fptr1 = fopen(filename,"r");
    fptr2 = fopen("output.txt","w");

    if(fptr1 == NULL || fptr2 == NULL)
    {
        printf("File cannot be opened.\n");
    }
    else 
    {
        printf("The file is now opened.\n");

        while(fgets(data , 3000 , fptr1) != 0)
        {
            int len = strlen(data)-1,i;
            if(len == 0)
            {
                fprintf(fptr2,"YES\n");
                continue;
            }
            int count = 0;
            for(i=0;i<len;i++)
            {
                if(data[i]=='(' || data[i]=='{'|| data[i]=='[' || data[i]=='<' || (data[i-1]=='('&& data[i]=='*'))
                {
                    push(data[i]);
                    if(data[i] != '*')
                        count += 1;
                }
                else if((data[i]==')'||data[i]=='}'||data[i]==']'||data[i]=='>'||(data[i]=='*'&&data[i+1]==')')))
                {
                    if(data[i] == '*' && top < 0)
                    {
                        count += 1;
                        break;
                    }
                    else if(top < 0 &&(data[i]==')'||data[i]=='}'||data[i]==']'||data[i]=='>'))
                    {
                        count += 1;
                        break;
                    }
                    else if(stack[top] == '*' && data[i] == '*')
                    {
                        pop();
                        pop();
                        count += 1;
                        i += 1;
                    }
                    else if(top >= 0 &&((stack[top]=='('&&data[i]==')')||(stack[top]=='{'&&data[i]=='}')||(stack[top]=='['&&data[i]==']')||((stack[top])=='<'&&data[i]=='>')))
                    {
                        pop();
                        count += 1;
                    }
                    else 
                    {
                        count += 1;
                        break;
                    }
                }
                else 
                {
                    count += 1;
                }
            }
            if(top <= 0 && (len == i || len +1 == i))
                fprintf(fptr2,"YES\n");
            else if(top >= 0 && len == i)
                fprintf(fptr2, "NO %d\n",count+1);
            else 
                fprintf(fptr2, "NO %d\n",count);
        }


        fclose(fptr1);
        fclose(fptr2);
        printf("\nThe output of the program has been written in output.txt\n");
        printf("File is now closed.\n");

    }
return 0;
}

