#include <bits/stdc++.h>

using namespace std;

string ltrim(const string &);
string rtrim(const string &);
vector<string> split(const string &);



int maximumStones(vector<int> arr) {
    int oddpos = 0, evenpos = 0, len = arr.size();

    for(int i = 0; i < len; i++)
    {
        if(i % 2 == 0)
        {
            evenpos += arr[i];
        }
        else 
        {
            oddpos += arr[i];
        }
    }

    while(evenpos != oddpos)
    {
        if(evenpos > oddpos)
        {
            --evenpos;
        }
        else
        {
            --oddpos;
        }
    }

    return evenpos * 2;
}

int main()
{
    ofstream fout(getenv("OUTPUT_PATH"));

    string n_temp;
    getline(cin, n_temp);

    int n = stoi(ltrim(rtrim(n_temp)));

