    {
        for(j=i+1;j<=n;j++)
        {
            if((i&j) > max1&& (i&j)< k)
            {
                max1=(i&j);
            }

            if((i|j) > max2&& (i|j)< k)
            {
                max2=(i|j);
            }

            if((i^j) > max3&& (i^j)< k)
            {
