int front(int n, int p)
{
    int i,j,a,c = 0;
    if(p%2 !=0)
    {
        p++;
    }
    for(i=1;i<p;i=i+2)
    {
        c++;
    }
return c;
}
int back(int n,int p)
{
    int i,j,a,c =0;
    if(n%2 != 0)
    {
        n--;
    }
    for(i=n;i>p;i=i-2)
    {
        c++;
    }
return c;
}

