// Complete the howManyGames function below.
int howManyGames(int p, int d, int m, int s) {
    // Return the number of games you can buy
    int counter = 0;
    while(s >= p)
    {
        counter++;
        s =s -  p;
        p = (p - d) >= m ? p - d : m;
    }
    return counter;

}

